mapper.select:
select t1.row_id id, t1.created created, t1.menu_name text, '' state, t1.menu_iconcls iconCls, nvl(t1.menu_url, t2.menu_url) menuUrl, t1.menu_grade menuGrade, t1.menu_icon menuIcon, t1.menu_type menuType, t1.sort_number sortNumber, t1.parent_menu_id parentMenuId, t2.menu_name parentMenuName, case upper(t1.OAUTH) WHEN 'OTHER' then 'OTHER_' || t1.row_id ELSE t1.OAUTH END oauthType, '' oauthSql, t1.comments comments, t1.readonly_flag readonlyFlag, t1.edit_flag editFlag, t1.add_flag addFlag, t1.delete_flag deleteFlag, t1.export_flag exportFlag, t1.platform_type platformType, t1.universal_ind universalInd, t1.fmcg_ind fmcgInd, t1.high_tech_ind highTechInd, t1.home_ind homeInd, t1.fin_ind finInd, t1.retail_ind retailInd, t1.auto_ind autoInd,
t3.menu_read_only dutyReadonlyFlag, t3.menu_add dutyAddFlag, t3.menu_edit dutyEditFlag, t3.menu_del dutyDeleteFlag, t3.menu_export dutyExportFlag, t3.duty_id dutyId from LNK_MENU t1, LNK_MENU t2, LNK_INTER_DUTY_MENU T3, LNK_INTER_DUTY_USER T4 where 1 = 1 and t1.parent_menu_id = t2.row_id(+) and t1.menu_grade = '2'
and T1.menu_name = #{text}
and T1.menu_url = #{menuUrl}
and T1.menu_grade = #{menuGrade}
and T1.menu_icon = #{menuIcon}
and T1.menu_type = #{menuType}
and T1.parent_menu_id = #{parentMenuId}
AND T3.MENU_ID = t1.ROW_ID AND T3.DUTY_ID = T4.DUTY_ID AND T4.USER_ID = #{attr1} ORDER BY t1.sort_number ASC
