mapper.sql:
	FROM LNK_MSG_USER T1, LNK_EMP_INFO T2, LNK_MESSAGE T3
   WHERE T1.is_delete='N' 
     AND T1.user_id=T2.row_id 
     AND T1.msg_id=T3.row_id
     AND T1.corp_id = #{usercorpid}
     and T1.msg_id = #{msgId}

mapper.select:
SELECT T1.ROW_ID as id, t1.created as created, T1.CREATED_BY as createdBy, TO_CHAR(T1.LAST_UPD, 'yyyy-mm-dd hh24:mi:ss') as lastUpdated, T1.LAST_UPD_BY as lastUpdatedBy, T1.CORP_ID corpid,
T1.user_id AS userId, T1.msg_id AS msgId, T1.is_read AS isRead, T1.read_comments AS readComments, TO_CHAR(T1.read_time,'YYYY-MM-DD HH24:MI') AS readTime, T2.username AS userName, T2.fst_name AS firstName, T3.msg_code AS msgCode, T3.msg_title AS msgTitle, T3.msg_content AS msgContent, T3.MSG_IMPORTANCE importance, T3.msg_type AS msgType, T3.msg_receive_type AS msgReceiveType, T3.is_delete AS isDelete, T3.is_send AS isSend, TO_CHAR(T3.MSG_SEND_TIME,'YYYY-MM-DD HH24:MI') AS sendTime, T3.SEND_BY AS sendBy, T3.SENDBY_ID AS sendById, 'receive' sendOrReceive FROM LNK_MSG_USER T1, LNK_EMP_INFO T2, LNK_MESSAGE T3 WHERE T1.is_delete='N' AND T1.user_id=T2.row_id AND T1.msg_id=T3.row_id and t1.user_id=#{attr1} and t3.sendby_id != #{attr1} union all select T3.ROW_ID as id, t3.created as created, T3.CREATED_BY as createdBy, TO_CHAR(T3.LAST_UPD, 'yyyy-mm-dd hh24:mi:ss') as lastUpdated, T3.LAST_UPD_BY as lastUpdatedBy, T3.CORP_ID corpid,
'' userId, T3.Row_Id msgId, '' isRead, '' readComments, TO_CHAR('','YYYY-MM-DD HH24:MI') AS readTime, T2.username userName, T2.fst_name firstName, T3.msg_code msgCode, T3.msg_title msgTitle, T3.msg_content AS msgContent, T3.MSG_IMPORTANCE importance, T3.msg_type AS msgType, T3.msg_receive_type AS msgReceiveType, T3.is_delete AS isDelete, T3.is_send AS isSend, TO_CHAR(T3.MSG_SEND_TIME,'YYYY-MM-DD HH24:MI') AS sendTime, T3.SEND_BY AS sendBy, T3.SENDBY_ID AS sendById, 'send' sendOrReceive from lnk_message T3, LNK_EMP_INFO T2 where T3.sendby_id=T2.Row_Id and t3.sendby_id = #{attr1}

mapper.insert:
  	INSERT INTO LNK_MSG_USER
	  (row_id,
       msg_id,
       user_id
	  )
	VALUES
	  (#{id},
	  #{msgId},
	  #{userId}
	 )

mapper.update:
  	 UPDATE LNK_MSG_USER
        SET
	       READ_COMMENTS = #{readComments}

      WHERE row_id = #{id}

UPDATE LNK_MSG_USER SET is_read = 'Y', read_time = sysdate WHERE row_id in
#{id}
and is_read <> 'Y'
