mapper.select:
SELECT T1.ROW_ID as id /*记录编号*/ ,T1.CREATED as created /*创建时间*/ ,T1.CREATED_BY as createdBy /*创建者ID*/ ,DATE_FORMAT(t1.LAST_UPD, '%Y-%m-%d %H:%i:%S') AS lastUpdated /*最后更新时间*/ ,T1.LAST_UPD_BY as lastUpdatedBy /*最后更新者ID*/ ,T1.CORP_ID as corpid /*公司账套*/ ,T1.ORG_ID as orgId /*组织ID*/ ,T1.POSTN_ID as postnId /*职位ID*/ ,T1.URL as url /*URL*/ ,T1.NAME as name /*名称*/ ,T1.COMMENTS as comments /*备注*/ ,T1.INSERT_FLAG as insertFlag /*添加*/ ,T1.DEL_FLAG as delFlag /*删除*/ ,T1.UPDATE_FLAG as updateFlag /*修改*/ ,T1.SELECT_FLAG as selectFlag /*查询*/ ,T1.DEF_ACCESS_FLAG as defaultAccessFlag /*默认可访问*/ ,T1.TYPE as type FROM LNK_URL_INFO T1, LNK_EMP_INFO T2, LNK_DUTY T3, LNK_INTER_DUTY_USER T4, LNK_URL_DUTY_INTER T5
WHERE T3.ROW_ID = T4.DUTY_ID AND T2.ROW_ID = T4.USER_ID AND T3.ROW_ID = T5.DUTY_ID AND T1.ROW_ID = T5.URL_ID
AND T1.URL = #{url}
AND T1.TYPE = #{type}
AND T2.ROW_ID = #{attr1}
AND T5.ACCESS_FLAG = #{attr2}
